import { defineComponent, renderSlot, toRefs } from 'vue'

import {
  configHoverDelay,
  configIcons,
  configLocale,
  configProps,
  configZIndex,
  useNameHelper
} from '@pocui/config'
import { configProviderProps } from './props'

export default defineComponent({
  name: 'ConfigProvider',
  description:'配置注入用于为其内部组件的提供各项配置。',
  categories: ['other'],
  props: configProviderProps,
  setup(_props, { slots }) {
    const { props, locale, icons, zIndex, theme, hoverDelay } = toRefs(_props)

    const nh = useNameHelper('config-provider')

    configProps(props)
    configLocale(locale)
    configIcons(icons)
    configZIndex(zIndex)
    configHoverDelay(hoverDelay)

    return () => {
      if (theme.value) {
        return <section class={[nh.b(), nh.ns(`theme-vars-${theme.value}`)]}>{renderSlot(slots, 'default')}</section>
      }

      return renderSlot(slots, 'default')
    }
  }
})

import { wrapProps } from '@pocui/config'

import type { PropsOptions } from '@/components/props'
import type { IconsOptions, LocaleOptions } from '@pocui/config'

import type { ExtractPropTypes, PropType } from 'vue'

export const configProviderProps = wrapProps({
  props: {
    type: Object as PropType<PropsOptions>,
    default: () => ({})
  },
  locale: {
    type: Object as PropType<LocaleOptions>,
    default: () => ({})
  },
  icons: {
    type: Object as PropType<IconsOptions>,
    default: () => ({})
  },
  zIndex: {
    type: Number,
    default: null
  },
  theme: {
    // eslint-disable-next-line @typescript-eslint/ban-types
    type: String as PropType<'base' | 'dark' | (string & {})>,
    default: null
  },
  hoverDelay: {
    type: Number,
    default: null
  }
})

export type ConfigProviderProps = ExtractPropTypes<typeof configProviderProps>
